import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import Recipe from "./Recipe.js";

const App = () => {
  const APP_ID = "54f5fb62";
  const APP_KEY = "ed7c473e53f990d85f72af084a155be0";

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");

  useEffect( () => {
    getRecipes();
  }, [search]);

  const getRecipes = async () => {
    const response = await fetch(`https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}`);
    const data = await response.json();
    setRecipes(data.hits);
  }

  const updateSearch = e => {
    setSearch(e.target.value);
  }
  return (
    <div className="App">
      <form>
        <input type="text" value={search} onChange={updateSearch}></input>
        <button type="submit">Search</button>
      </form>
      {recipes.map(recipe => (
        <Recipe title={recipe.recipe.label} calories={recipe.recipe.calories} image={recipe.recipe.image}></Recipe>
      ))}
    </div>
  );
};

export default App;
